mapper.update:
UPDATE food
`name` = #{name}, image = #{image}, price = #{price}, info = #{info}, menu_classify_id = #{menuClassifyId}, `order` = #{order}, stock = #{stock}, weight_list = #{weightList}, taste_list = #{tasteList}, practice_list = #{practiceList}, update_time = CURRENT_TIMESTAMP ()
WHERE id = #{id}

mapper.select:

	SELECT COUNT(id) FROM food where 1=1 
	AND menu_classify_id = #{classify}
	AND name = #{foodName}
